mapper.select:
SELECT

FROM mj_download_access_para_info a

WHERE a.id = #{id}

select count(1) from mj_download_access_para_info where
access_para_info_id=#{accessParaInfoId} AND equipment_id= #{DownloadAccessParaInfo} AND download_type=#{downloadType}
AND is_download=#{isDownload}

mapper.update:
UPDATE mj_download_access_para_info SET
access_para_info_id =
#{accessParaInfoId}
WHERE id = #{id}



DELETE FROM mj_download_access_para_info
WHERE access_para_info_id = #{accessParaInfoId} and
is_download=#{isDownload}

	and equipment_id=#{equipmentId}
