mapper.select:
SELECT

FROM mj_download_workday_para_info a

WHERE a.id = #{id}

SELECT
FROM mj_download_workday_para_info a
1=1 AND o3.id in
${item}
AND o3.id =#{office.id}
AND (a.download_time >= #{downloadTime} or a.download_time is NULL )
AND (#{downloadTimeTwo} >= a.download_time or a.download_time is NULL )
ORDER BY ${page.orderBy}
order by a.register_time desc

select count(1) from mj_download_workday_para_info where
workday_para_info_id=#{workdayParaInfoId} AND equipment_id= #{equipmentId}
AND download_type=#{downloadType}
AND is_download=#{isDownload}

mapper.update:
UPDATE mj_download_workday_para_info SET
workday_para_info_id =
#{workdayParaInfoId}
WHERE id = #{id}



DELETE FROM mj_download_workday_para_info
WHERE access_para_info_id = #{accessParaInfoId} and
is_download=#{isDownload}

	and equipment_id=#{equipmentId}
