mapper.select:
SELECT
FROM guard_staff a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND (
a.nManage_id IN
${item}
) and a.staff_type in ('0', '2')
AND (o3.parent_ids LIKE '%,'||#{office.id}||',%' '%,'+#{office.id}+',%' CONCAT('%,', #{office.id}, ',%') OR a.nManage_id = #{office.id}) and a.staff_type = '1'
AND a.identify_number = #{identifyNumber}
AND a.work_num LIKE '%'||#{workNum}||'%' '%'+#{workNum}+'%' concat('%',#{workNum},'%')
AND a.area_id = #{area.id}
and a.staff_type in ('0') and a.status in('2', '3') and a.work_status = '1' and not exists( select 1 from guard_class_person_info g where g.person_id = a.id and g.class_task_id != #{classTaskId} )
and a.staff_type in ('2') and a.status in('2', '3') and a.work_status = '1'
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
count(1)
FROM guard_staff a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.work_num=#{workNum}


		AND a.identify_number=#{identifyNumber}


		and a.id != #{id}

SELECT

FROM guard_staff a


	${sqlMap.dsf}
	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE guard_staff SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select * from guard_staff

	del_flag='0' and id not in (
		select staff_id
		from mj_authorization
		where access_para_info_id=#{accessParaInfoId}
	)

		and name like CONCAT('%', #{name}, '%')


		and work_num like CONCAT('%', #{workNum}, '%')



	limit #{pageIndex},#{size}
