mapper.select:
SELECT

FROM guard_task_schedule_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id LIKE
		'%'||#{id}||'%'
		'%'+#{id}+'%'
		concat('%',#{id},'%')


		AND a.class_task_id = #{classTaskId}


		AND b.name LIKE
		'%'||#{classTaskInfo.name}||'%'
		'%'+#{classTaskInfo.name}+'%'
		concat('%',#{classTaskInfo.name},'%')


		AND a.task_type = #{taskType}


		and not exists(
			select 1 from guard_connect_event gce
			where gce.task_id = a.id
		)

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM guard_task_schedule_info a


	${sqlMap.dsf}
	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE guard_task_schedule_info SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
